mapper.update:
UPDATE TBL_BOARD
   SET BRDTITLE=#{brdtitle}
     , BRDWRITER=#{brdwriter}
     , BRDMEMO=#{brdmemo} 
 WHERE BRDDELETEFLAG='N'
   AND BRDNO=#{brdno}

UPDATE TBL_BOARD
   SET BRDHIT = BRDHIT + 1 
 WHERE BRDNO=#{brdno}

mapper.delete:
UPDATE TBL_BOARD
   SET BRDDELETEFLAG='Y' 
 WHERE BRDNO=#{brdno}


        DELETE 
          FROM TBL_BOARDFILE
         WHERE FILENO IN (

		        	 ${item}

		)
